styles.css.content:
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%);
    color: #e0e6ed;
    line-height: 1.6;
    overflow-x: hidden;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* Header Styles */
.header {
    background: rgba(26, 26, 46, 0.95);
    backdrop-filter: blur(10px);
    box-shadow: 0 2px 20px rgba(233, 69, 96, 0.3);
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 1000;
    transition: all 0.3s ease;
    border-bottom: 2px solid rgba(233, 69, 96, 0.5);
}

.navbar {
    padding: 1rem 0;
}

.nav-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.store-logo-img {
    height: 40px;
    width: auto;
    filter: drop-shadow(0 0 10px rgba(233, 69, 96, 0.3));
    transition: all 0.3s ease;
}

.store-logo-img:hover {
    filter: drop-shadow(0 0 15px rgba(233, 69, 96, 0.6));
    transform: scale(1.05);
}

.store-logo .store-name {
    font-size: 1.5rem;
    font-weight: bold;
    color: #e94560;
    text-decoration: none;
    background: linear-gradient(45deg, #e94560 0%, #f38ba8 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: glow 2s ease-in-out infinite alternate;
    text-shadow: 0 0 20px rgba(233, 69, 96, 0.5);
    z-index: 10;
    position: relative;
}

.highlight {
    color: #e94560;
    text-shadow: 0 0 15px rgba(233, 69, 96, 0.8);
    animation: pulse 2s ease-in-out infinite;
}

@keyframes glow {
    from {
        text-shadow: 0 0 20px rgba(233, 69, 96, 0.5);
    }
    to {
        text-shadow: 0 0 30px rgba(243, 139, 168, 0.8);
    }
}

@keyframes pulse {
    0%, 100% {
        text-shadow: 0 0 15px rgba(233, 69, 96, 0.8);
    }
    50% {
        text-shadow: 0 0 25px rgba(233, 69, 96, 1), 0 0 35px rgba(243, 139, 168, 0.8);
    }
}

.nav-menu {
    display: flex;
    gap: 2rem;
}

.nav-link {
    text-decoration: none;
    color: #e0e6ed;
    font-weight: 500;
    transition: all 0.3s ease;
    position: relative;
}

.nav-link:hover {
    color: #e94560;
    transform: translateY(-2px);
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(45deg, #e94560, #f38ba8);
    transition: width 0.3s ease;
}

.nav-link:hover::after {
    width: 100%;
}

.nav-actions {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.search-container {
    position: relative;
}

.search-input {
    padding: 0.5rem 2.5rem 0.5rem 1rem;
    border: 2px solid #e1e8ed;
    border-radius: 25px;
    background: white;
    transition: all 0.3s ease;
    width: 200px;
}

.search-input:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
    transform: scale(1.02);
}

.search-icon {
    position: absolute;
    right: 1rem;
    top: 50%;
    transform: translateY(-50%);
    color: #667eea;
}

.cart-btn {
    background: linear-gradient(45deg, #667eea, #764ba2);
    color: white;
    border: none;
    padding: 0.75rem 1rem;
    border-radius: 50px;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
    font-size: 1rem;
}

.cart-btn:hover {
