    position: static;
    overflow: hidden;
}

.hero-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: center;
    position: static;
}

.hero-title {
    font-size: 3.5rem;
    font-weight: bold;
    margin-bottom: 1rem;
    color: #e0e6ed;
    line-height: 1.2;
    text-shadow: 0 0 20px rgba(233, 69, 96, 0.5);
}

.hero-subtitle {
    font-size: 1.25rem;
    color: #b0b7c3;
    margin-bottom: 2rem;
}

.cta-btn {
    background: linear-gradient(45deg, #e94560, #f38ba8);
    color: white;
    border: none;
    padding: 1rem 2rem;
    font-size: 1.1rem;
    border-radius: 50px;
