}

.cta-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 35px rgba(233, 69, 96, 0.4);
}

.hero-animation {
    display: none;
}

@keyframes float {
    0%, 100% {
        transform: translateY(0px) rotate(0deg);
    }
    50% {
        transform: translateY(-20px) rotate(180deg);
    }
}

/* Categories Section */
.categories {
    padding: 40px 0 80px;
    background: rgba(22, 33, 62, 0.5);
}

.section-title {
    text-align: center;
    font-size: 2.5rem;
    color: #e0e6ed;
    margin-bottom: 3rem;
    position: relative;
    text-shadow: 0 0 20px rgba(233, 69, 96, 0.3);
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 4px;
    background: linear-gradient(45deg, #e94560, #f38ba8);
    border-radius: 2px;
}

.categories-grid {
    display: grid;
