}

.filter-btn {
    background: white;
    border: 2px solid #e1e8ed;
    padding: 0.75rem 1.5rem;
    border-radius: 25px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-weight: 500;
}

.filter-btn:hover,
.filter-btn.active {
    background: linear-gradient(45deg, #e94560, #f38ba8);
