    position: relative;
    border: 1px solid rgba(233, 69, 96, 0.3);
    backdrop-filter: blur(10px);
}

.product-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px rgba(233, 69, 96, 0.3);
    border-color: #e94560;
}

.product-image {
    height: 200px;
    background: linear-gradient(45deg, #e94560, #f38ba8);
