    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    margin-top: 3rem;
}

.event-card {
    background: rgba(26, 26, 46, 0.9);
    border-radius: 20px;
    padding: 2rem;
    text-align: center;
    box-shadow: 0 15px 35px rgba(233, 69, 96, 0.2);
    transition: all 0.3s ease;
    border: 2px solid rgba(233, 69, 96, 0.3);
    backdrop-filter: blur(10px);
}

.event-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 25px 50px rgba(233, 69, 96, 0.4);
    border-color: #e94560;
}

.event-icon {
    width: 80px;
    height: 80px;
    margin: 0 auto 1.5rem;
    background: linear-gradient(45deg, #e94560, #f38ba8);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    color: white;
    transition: all 0.3s ease;
}

.event-card:hover .event-icon {
    transform: scale(1.1) rotate(10deg);
}

.event-card h3 {
    font-size: 1.5rem;
    color: #e0e6ed;
    margin-bottom: 1rem;
    text-shadow: 0 0 10px rgba(233, 69, 96, 0.3);
}

.event-card p {
    color: #b0b7c3;
    line-height: 1.6;
    margin-bottom: 1.5rem;
}

.event-details {
    margin-bottom: 2rem;
}

.event-detail {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    margin-bottom: 0.5rem;
    color: #e0e6ed;
    font-weight: 500;
}

.event-detail i {
    color: #e94560;
    width: 16px;
}

.event-register-btn {
    background: linear-gradient(45deg, #25d366, #128c7e);
    color: white;
    border: none;
    padding: 1rem 2rem;
    border-radius: 25px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-weight: bold;
    font-size: 1rem;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    width: 100%;
}

.event-register-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 15px 35px rgba(37, 211, 102, 0.4);
}

/* Footer */
.footer {
    background: #1a1a2e;
    color: #e0e6ed;
    padding: 3rem 0 1rem;
    border-top: 2px solid rgba(233, 69, 96, 0.3);
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-bottom: 2rem;
}

.footer-section h3,
.footer-section h4 {
    margin-bottom: 1rem;
}

.footer-section p {
    margin-bottom: 0.5rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.social-links {
    display: flex;
    gap: 1rem;
}

.social-links a {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
    background: #e94560;
    color: white;
    border-radius: 50%;
    text-decoration: none;
    transition: all 0.3s ease;
}

.social-links a:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 25px rgba(233, 69, 96, 0.3);
}

.footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid #34495e;
    color: #bdc3c7;
}

/* Responsive Design */
@media (max-width: 768px) {
    .nav-menu {
        display: none;
        position: absolute;
        top: 100%;
        left: 0;
        right: 0;
        background: rgba(26, 26, 46, 0.95);
        flex-direction: column;
        padding: 1rem 0;
        box-shadow: 0 5px 15px rgba(233, 69, 96, 0.3);
        backdrop-filter: blur(10px);
        border-bottom: 2px solid rgba(233, 69, 96, 0.5);
    }

    .about-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .about-map iframe {
        height: 300px;
    }

    .events-grid {
        grid-template-columns: 1fr;
        gap: 1.5rem;
    }

    .event-card {
        padding: 1.5rem;
    }

    .nav-menu.active {
        display: flex;
    }

    .mobile-menu-btn {
        display: block;
    }

    .search-input {
        width: 120px;
        font-size: 0.9rem;
    }

    .nav-actions {
        gap: 0.5rem;
    }

    .cart-btn {
        padding: 0.5rem 0.75rem;
        font-size: 0.9rem;
    }

    .hero-content {
        grid-template-columns: 1fr;
        text-align: center;
        gap: 2rem;
        padding: 0 10px;
    }

    .hero-animation {
        height: 300px;
    }

    .hero-title {
        font-size: 2.5rem;
    }

    .categories-grid {
        grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    }

    .products-grid {
        grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    }

    .cart-sidebar {
        width: 100%;
        right: -100%;
    }

    .filter-controls {
        gap: 0.5rem;
    }

    .filter-btn {
        padding: 0.5rem 1rem;
        font-size: 0.9rem;
    }

    .product-actions {
        flex-direction: column;
        gap: 0.5rem;
    }

    .quantity-selector {
        align-self: center;
    }
}

@media (max-width: 480px) {
    .store-name {
        font-size: 1.3rem;
    }

    .nav-container {
        padding: 0 10px;
    }

    .search-input {
        width: 100px;
        padding: 0.4rem 2rem 0.4rem 0.8rem;
    }

    .cart-btn {
        padding: 0.4rem 0.6rem;
    }

    .hero {
        padding: 100px 0 60px;
    }

    .hero-title {
        font-size: 1.8rem;
        line-height: 1.3;
    }

    .hero-subtitle {
        font-size: 1rem;
    }

    .cta-btn {
        padding: 0.8rem 1.5rem;
        font-size: 1rem;
    }

    .section-title {
        font-size: 1.8rem;
    }

    .container {
        padding: 0 15px;
    }

    .categories-grid {
        grid-template-columns: 1fr;
        gap: 1rem;
    }

    .category-card {
        padding: 1.5rem;
    }

    .products-grid {
        grid-template-columns: 1fr;
        gap: 1rem;
    }

    .product-card {
        margin: 0 5px;
    }

    .nav-actions {
        gap: 0.3rem;
    }

    .events-description {
        font-size: 1rem;
        padding: 0 10px;
    }

    .coming-soon-badge {
        padding: 0.5rem 1rem;
        font-size: 0.9rem;
    }

    .event-register-btn {
        padding: 0.8rem 1.5rem;
        font-size: 0.9rem;
    }

    .about-text h3 {
        font-size: 1.5rem;
    }

    .about-text p {
        font-size: 0.95rem;
    }

    .hero-animation {
        display: none;
    }

    .search-input {
        width: 120px;
    }
}

/* Animations */
.fade-in {
    animation: fadeIn 0.6s ease-in-out;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.slide-up {
    animation: slideUp 0.6s ease-in-out;
}

@keyframes slideUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Loading Animation */
.loading {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 3px solid rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    border-top-color: #fff;
    animation: spin 1s ease-in-out infinite;
}

@keyframes spin {
    to {
        transform: rotate(360deg);
    }
}

/* Scrollbar Styling */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(45deg, #667eea, #764ba2);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(45deg, #5a6fd8, #6a4190);
}
